function hideErrors(){$(".error").each(function(){$(this).css("opacity",0)})}function revealErrors(){$(".error").each(function(){$(this).animate({opacity:1})})}function toggleFieldSet(e,t,o,i){console.log(e,t,o,i),$(e).is(":checked")?($(o).hide(0),$(t).fadeIn(i)):($(t).fadeOut(i),$(o).show(0))}function checkboxTogglesSet(e,t,o){toggleFieldSet(e,t,o,0),$(e).change(function(){toggleFieldSet(e,t,o,"fast")})}function addTooltips(e,t,o){$(e).qtip({style:{classes:"qtip-blue qtip-rounded"},position:{at:t,my:o,viewport:$(window)},hide:{fixed:!0,delay:300,effect:{length:50}},show:{delay:50,effect:{length:50}}})}